import React, { useState, useEffect, useRef, useMemo, useCallback } from 'react';
import './Admin.css';
import { 
  autoSaveReservations, 
  autoSaveMenu, 
  autoSavePhotos, 
  loadTheme, 
  loadMenu, 
  loadReservations, 
  loadCategories,
  loadPhotos,
  saveTheme,
  saveCategories
} from '../utils/fileStorage';
import { defaultMenu } from '../data/defaultMenu';

// Ð‘Ð°Ð·Ð¾Ð²Ñ‹Ðµ ÐºÐ°Ñ‚ÐµÐ³Ð¾Ñ€Ð¸Ð¸ Ð¿Ð¾ ÑƒÐ¼Ð¾Ð»Ñ‡Ð°Ð½Ð¸ÑŽ
const defaultCategories = [
  { id: 1, key: 'cold', name: 'Ð¥Ð¾Ð»Ð¾Ð´Ð½Ñ‹Ðµ Ð·Ð°ÐºÑƒÑÐºÐ¸', order: 1 },
  { id: 2, key: 'salads', name: 'Ð¡Ð°Ð»Ð°Ñ‚Ñ‹', order: 2 },
  { id: 3, key: 'hot', name: 'Ð“Ð¾Ñ€ÑÑ‡Ð¸Ðµ Ð±Ð»ÑŽÐ´Ð°', order: 3 },
  { id: 4, key: 'drinks', name: 'ÐÐ°Ð¿Ð¸Ñ‚ÐºÐ¸', order: 4 }
];

// Ð‘Ð°Ð·Ð¾Ð²Ñ‹Ðµ Ñ‚ÐµÐ¼Ñ‹ Ð¿Ð¾ ÑƒÐ¼Ð¾Ð»Ñ‡Ð°Ð½Ð¸ÑŽ
const defaultThemes = {
  elegant: {
    id: 'elegant',
    name: 'Ð­Ð»ÐµÐ³Ð°Ð½Ñ‚Ð½Ð°Ñ ÑÐ¸Ð½ÑÑ',
    primaryColor: '#2C3E50',
    primaryLight: '#34495E',
    primaryDark: '#1C2833',
    secondaryColor: '#3498DB',
    secondaryLight: '#5DADE2',
    secondaryDark: '#2980B9',
    accentColor: '#E74C3C',
    backgroundColor: '#ECF0F1',
    textColor: '#2C3E50',
    textLight: '#566573'
  }
};

const Admin = () => {
  // Ð¤ÑƒÐ½ÐºÑ†Ð¸Ñ Ð´Ð»Ñ Ð³ÐµÐ½ÐµÑ€Ð°Ñ†Ð¸Ð¸ ÑƒÐ½Ð¸ÐºÐ°Ð»ÑŒÐ½Ð¾Ð³Ð¾ ID
  const generateUniqueId = (existingItems) => {
    let newId;
    let attempts = 0;
    const maxAttempts = 100;

    do {
      // Ð˜ÑÐ¿Ð¾Ð»ÑŒÐ·ÑƒÐµÐ¼ timestamp + ÑÐ»ÑƒÑ‡Ð°Ð¹Ð½Ð¾Ðµ Ñ‡Ð¸ÑÐ»Ð¾ Ð´Ð»Ñ ÑƒÐ½Ð¸ÐºÐ°Ð»ÑŒÐ½Ð¾ÑÑ‚Ð¸
      newId = Date.now() + Math.floor(Math.random() * 1000);
      attempts++;

      // ÐŸÑ€Ð¾Ð²ÐµÑ€ÑÐµÐ¼, Ñ‡Ñ‚Ð¾ Ñ‚Ð°ÐºÐ¾Ð³Ð¾ ID Ð½ÐµÑ‚ Ð² ÑÑƒÑ‰ÐµÑÑ‚Ð²ÑƒÑŽÑ‰Ð¸Ñ… ÑÐ»ÐµÐ¼ÐµÐ½Ñ‚Ð°Ñ…
      const exists = existingItems.some(item => item.id === newId);
      if (!exists) {
        return newId;
      }
    } while (attempts < maxAttempts);

    // Ð•ÑÐ»Ð¸ Ð½Ðµ ÑƒÐ´Ð°Ð»Ð¾ÑÑŒ Ð½Ð°Ð¹Ñ‚Ð¸ ÑƒÐ½Ð¸ÐºÐ°Ð»ÑŒÐ½Ñ‹Ð¹ ID Ð·Ð° 100 Ð¿Ð¾Ð¿Ñ‹Ñ‚Ð¾Ðº, Ð¸ÑÐ¿Ð¾Ð»ÑŒÐ·ÑƒÐµÐ¼ Ð¿Ñ€Ð¾ÑÑ‚Ð¾ timestamp
    console.warn('âš ï¸ ÐÐµ ÑƒÐ´Ð°Ð»Ð¾ÑÑŒ ÑÐ³ÐµÐ½ÐµÑ€Ð¸Ñ€Ð¾Ð²Ð°Ñ‚ÑŒ ÑƒÐ½Ð¸ÐºÐ°Ð»ÑŒÐ½Ñ‹Ð¹ ID Ð·Ð° 100 Ð¿Ð¾Ð¿Ñ‹Ñ‚Ð¾Ðº, Ð¸ÑÐ¿Ð¾Ð»ÑŒÐ·ÑƒÐµÐ¼ timestamp');
    return Date.now();
  };

  // ÐŸÑ€Ð¾Ð²ÐµÑ€ÑÐµÐ¼ Ð°Ð²Ñ‚Ð¾Ñ€Ð¸Ð·Ð°Ñ†Ð¸ÑŽ Ð¸Ð· localStorage Ð¿Ñ€Ð¸ Ð·Ð°Ð³Ñ€ÑƒÐ·ÐºÐµ
  const [isAuthenticated, setIsAuthenticated] = useState(() => {
    return localStorage.getItem('admin-authenticated') === 'true';
  });

  const [activeTab, setActiveTab] = useState('reservations');
  const [reservations, setReservations] = useState([]);
  const [menuItems, setMenuItems] = useState([]);
  const [categories, setCategories] = useState([]);
  const [themes, setThemes] = useState({});
  const [currentTheme, setCurrentTheme] = useState('default');
  const [photos, setPhotos] = useState([]);
  const [editingItem, setEditingItem] = useState(null);
  const [editingCategory, setEditingCategory] = useState(null);
  const [editingTheme, setEditingTheme] = useState(null);
  const [editingPhoto, setEditingPhoto] = useState(null);
  const newPhotoKeyRef = useRef(`new-photo-${Date.now()}`); // Ð¡Ñ‚Ð°Ð±Ð¸Ð»ÑŒÐ½Ñ‹Ð¹ key Ð´Ð»Ñ Ð½Ð¾Ð²Ð¾Ð¹ Ñ„Ð¾Ñ€Ð¼Ñ‹
  const newPhotoObjectRef = useRef({}); // Ð¡Ñ‚Ð°Ð±Ð¸Ð»ÑŒÐ½Ñ‹Ð¹ Ð¾Ð±ÑŠÐµÐºÑ‚ Ð´Ð»Ñ Ð½Ð¾Ð²Ð¾Ð¹ Ñ„Ð¾Ñ€Ð¼Ñ‹
  // Ð“Ð»Ð¾Ð±Ð°Ð»ÑŒÐ½Ð¾Ðµ Ñ…Ñ€Ð°Ð½Ð¸Ð»Ð¸Ñ‰Ðµ Ð´Ð»Ñ Ð·Ð°Ð³Ñ€ÑƒÐ¶ÐµÐ½Ð½Ñ‹Ñ… Ð¸Ð·Ð¾Ð±Ñ€Ð°Ð¶ÐµÐ½Ð¸Ð¹ (Ð²Ð½Ðµ ÐºÐ¾Ð¼Ð¿Ð¾Ð½ÐµÐ½Ñ‚Ð°, Ñ‡Ñ‚Ð¾Ð±Ñ‹ Ð½Ðµ Ñ‚ÐµÑ€ÑÑ‚ÑŒ Ð¿Ñ€Ð¸ Ð¿ÐµÑ€ÐµÑ€ÐµÐ½Ð´ÐµÑ€Ðµ)
  const uploadedImagesStore = useRef(new Map()); // key -> base64 image
  const newMenuItemKeyRef = useRef(`new-menu-item-${Date.now()}`); // Ð¡Ñ‚Ð°Ð±Ð¸Ð»ÑŒÐ½Ñ‹Ð¹ key Ð´Ð»Ñ Ð½Ð¾Ð²Ð¾Ð¹ Ñ„Ð¾Ñ€Ð¼Ñ‹ Ð¼ÐµÐ½ÑŽ
  // Ð“Ð»Ð¾Ð±Ð°Ð»ÑŒÐ½Ð¾Ðµ Ñ…Ñ€Ð°Ð½Ð¸Ð»Ð¸Ñ‰Ðµ Ð´Ð»Ñ Ð·Ð°Ð³Ñ€ÑƒÐ¶ÐµÐ½Ð½Ñ‹Ñ… Ð¸Ð·Ð¾Ð±Ñ€Ð°Ð¶ÐµÐ½Ð¸Ð¹ Ð¼ÐµÐ½ÑŽ
  const menuImagesStore = useRef(new Map()); // key -> base64 image
  const [showNotification, setShowNotification] = useState(false);
  const [notificationMessage, setNotificationMessage] = useState('');
  const [notificationType, setNotificationType] = useState('success');

  // Ð¤ÑƒÐ½ÐºÑ†Ð¸Ñ Ð´Ð»Ñ Ð¿ÐµÑ€ÐµÐºÐ»ÑŽÑ‡ÐµÐ½Ð¸Ñ Ð²ÐºÐ»Ð°Ð´Ð¾Ðº Ñ Ð·Ð°ÐºÑ€Ñ‹Ñ‚Ð¸ÐµÐ¼ Ð²ÑÐµÑ… Ð¼Ð¾Ð´Ð°Ð»ÑŒÐ½Ñ‹Ñ… Ð¾ÐºÐ¾Ð½
  const handleTabChange = (tab) => {
    // Ð—Ð°ÐºÑ€Ñ‹Ð²Ð°ÐµÐ¼ Ð²ÑÐµ Ð¾Ñ‚ÐºÑ€Ñ‹Ñ‚Ñ‹Ðµ Ð¼Ð¾Ð´Ð°Ð»ÑŒÐ½Ñ‹Ðµ Ð¾ÐºÐ½Ð°
    // ÐžÑ‡Ð¸Ñ‰Ð°ÐµÐ¼ Ñ…Ñ€Ð°Ð½Ð¸Ð»Ð¸Ñ‰Ðµ Ð¿Ñ€Ð¸ Ð¿ÐµÑ€ÐµÐºÐ»ÑŽÑ‡ÐµÐ½Ð¸Ð¸ Ð²ÐºÐ»Ð°Ð´Ð¾Ðº
    if (editingItem) {
      const formKey = editingItem?.id || newMenuItemKeyRef.current;
      if (menuImagesStore.current.has(formKey)) {
        menuImagesStore.current.delete(formKey);
      }
    }
    setEditingItem(null);
    setEditingCategory(null);
    setEditingTheme(null);
    if (editingPhoto) {
      const formKey = editingPhoto?.id || newPhotoKeyRef.current;
      if (uploadedImagesStore.current.has(formKey)) {
        uploadedImagesStore.current.delete(formKey);
      }
    }
    setEditingPhoto(null);
    // ÐŸÐµÑ€ÐµÐºÐ»ÑŽÑ‡Ð°ÐµÐ¼ Ð²ÐºÐ»Ð°Ð´ÐºÑƒ
    setActiveTab(tab);
  };

  useEffect(() => {
    const loadCategoriesFromFile = async () => {
      try {
        const loadedCategories = await loadCategories();
        if (Array.isArray(loadedCategories)) {
          // ÐŸÑƒÑÑ‚Ð¾Ð¹ Ð¼Ð°ÑÑÐ¸Ð² - ÑÑ‚Ð¾ Ð²Ð°Ð»Ð¸Ð´Ð½Ð¾Ðµ ÑÐ¾ÑÑ‚Ð¾ÑÐ½Ð¸Ðµ (Ð¿Ð¾Ð»ÑŒÐ·Ð¾Ð²Ð°Ñ‚ÐµÐ»ÑŒ ÑƒÐ´Ð°Ð»Ð¸Ð» Ð²ÑÐµ ÐºÐ°Ñ‚ÐµÐ³Ð¾Ñ€Ð¸Ð¸)
          // ÐÐµ Ð¿Ð¾Ð´ÑÑ‚Ð°Ð²Ð»ÑÐµÐ¼ Ð´ÐµÑ„Ð¾Ð»Ñ‚Ð½Ñ‹Ðµ ÐºÐ°Ñ‚ÐµÐ³Ð¾Ñ€Ð¸Ð¸, ÐµÑÐ»Ð¸ Ð¼Ð°ÑÑÐ¸Ð² Ð¿Ñ€Ð¾ÑÑ‚Ð¾ Ð¿ÑƒÑÑ‚Ð¾Ð¹
          const sorted = [...loadedCategories].sort((a, b) => a.order - b.order);
          setCategories(sorted);
          console.log(`ðŸ“¥ loadCategoriesFromFile: Ð—Ð°Ð³Ñ€ÑƒÐ¶ÐµÐ½Ð¾ ÐºÐ°Ñ‚ÐµÐ³Ð¾Ñ€Ð¸Ð¹: ${sorted.length}`);
        } else {
          // Ð•ÑÐ»Ð¸ Ð·Ð°Ð³Ñ€ÑƒÐ¶ÐµÐ½Ð½Ñ‹Ðµ Ð´Ð°Ð½Ð½Ñ‹Ðµ Ð½Ðµ Ð¼Ð°ÑÑÐ¸Ð², Ð¸ÑÐ¿Ð¾Ð»ÑŒÐ·ÑƒÐµÐ¼ Ð´ÐµÑ„Ð¾Ð»Ñ‚Ð½Ñ‹Ðµ ÐºÐ°Ñ‚ÐµÐ³Ð¾Ñ€Ð¸Ð¸ Ñ‚Ð¾Ð»ÑŒÐºÐ¾ Ð¿Ñ€Ð¸ Ð¿ÐµÑ€Ð²Ð¾Ð¹ Ð·Ð°Ð³Ñ€ÑƒÐ·ÐºÐµ
          // ÐŸÑ€Ð¾Ð²ÐµÑ€ÑÐµÐ¼, ÐµÑÑ‚ÑŒ Ð»Ð¸ ÑƒÐ¶Ðµ ÑÐ¾Ñ…Ñ€Ð°Ð½ÐµÐ½Ð½Ñ‹Ðµ ÐºÐ°Ñ‚ÐµÐ³Ð¾Ñ€Ð¸Ð¸ Ð² localStorage
          const stored = localStorage.getItem('restaurant-categories');
          if (stored) {
            try {
              const parsed = JSON.parse(stored);
              if (Array.isArray(parsed)) {
                // Ð•ÑÐ»Ð¸ ÐµÑÑ‚ÑŒ ÑÐ¾Ñ…Ñ€Ð°Ð½ÐµÐ½Ð½Ñ‹Ðµ ÐºÐ°Ñ‚ÐµÐ³Ð¾Ñ€Ð¸Ð¸, Ð¸ÑÐ¿Ð¾Ð»ÑŒÐ·ÑƒÐµÐ¼ Ð¸Ñ… (Ð´Ð°Ð¶Ðµ ÐµÑÐ»Ð¸ Ð¿ÑƒÑÑ‚Ð¾Ð¹ Ð¼Ð°ÑÑÐ¸Ð²)
                console.warn('âš ï¸ loadCategoriesFromFile: Ð”Ð°Ð½Ð½Ñ‹Ðµ Ð½Ðµ ÑÐ²Ð»ÑÑŽÑ‚ÑÑ Ð¼Ð°ÑÑÐ¸Ð²Ð¾Ð¼, Ð½Ð¾ ÐµÑÑ‚ÑŒ ÑÐ¾Ñ…Ñ€Ð°Ð½ÐµÐ½Ð½Ñ‹Ðµ ÐºÐ°Ñ‚ÐµÐ³Ð¾Ñ€Ð¸Ð¸. Ð˜ÑÐ¿Ð¾Ð»ÑŒÐ·ÑƒÐµÐ¼ Ð¿ÑƒÑÑ‚Ð¾Ð¹ Ð¼Ð°ÑÑÐ¸Ð².');
                setCategories([]);
              } else {
                // Ð•ÑÐ»Ð¸ ÑÐ¾Ñ…Ñ€Ð°Ð½ÐµÐ½Ð½Ñ‹Ðµ Ð´Ð°Ð½Ð½Ñ‹Ðµ Ð½Ðµ Ð¼Ð°ÑÑÐ¸Ð², Ð¸ÑÐ¿Ð¾Ð»ÑŒÐ·ÑƒÐµÐ¼ Ð´ÐµÑ„Ð¾Ð»Ñ‚Ð½Ñ‹Ðµ ÐºÐ°Ñ‚ÐµÐ³Ð¾Ñ€Ð¸Ð¸
                console.log('ðŸ“¥ loadCategoriesFromFile: Ð¡Ð¾Ñ…Ñ€Ð°Ð½ÐµÐ½Ð½Ñ‹Ðµ Ð´Ð°Ð½Ð½Ñ‹Ðµ Ð½Ðµ Ð¼Ð°ÑÑÐ¸Ð², Ð¸ÑÐ¿Ð¾Ð»ÑŒÐ·ÑƒÐµÐ¼ Ð´ÐµÑ„Ð¾Ð»Ñ‚Ð½Ñ‹Ðµ ÐºÐ°Ñ‚ÐµÐ³Ð¾Ñ€Ð¸Ð¸');
                setCategories([...defaultCategories]);
              }
            } catch (e) {
              // Ð•ÑÐ»Ð¸ Ð´Ð°Ð½Ð½Ñ‹Ðµ Ð¿Ð¾Ð²Ñ€ÐµÐ¶Ð´ÐµÐ½Ñ‹, Ð¸ÑÐ¿Ð¾Ð»ÑŒÐ·ÑƒÐµÐ¼ Ð´ÐµÑ„Ð¾Ð»Ñ‚Ð½Ñ‹Ðµ ÐºÐ°Ñ‚ÐµÐ³Ð¾Ñ€Ð¸Ð¸
              console.log('ðŸ“¥ loadCategoriesFromFile: Ð¡Ð¾Ñ…Ñ€Ð°Ð½ÐµÐ½Ð½Ñ‹Ðµ Ð´Ð°Ð½Ð½Ñ‹Ðµ Ð¿Ð¾Ð²Ñ€ÐµÐ¶Ð´ÐµÐ½Ñ‹, Ð¸ÑÐ¿Ð¾Ð»ÑŒÐ·ÑƒÐµÐ¼ Ð´ÐµÑ„Ð¾Ð»Ñ‚Ð½Ñ‹Ðµ ÐºÐ°Ñ‚ÐµÐ³Ð¾Ñ€Ð¸Ð¸');
              setCategories([...defaultCategories]);
            }
