      } catch (storageError) {
        console.warn(`âš ï¸ loadDataFromFile: ÐÐµ ÑƒÐ´Ð°Ð»Ð¾ÑÑŒ ÑÐ¾Ñ…Ñ€Ð°Ð½Ð¸Ñ‚ÑŒ ${filename} Ð² localStorage:`, storageError);
      }
