    return { success: true, data };
  } catch (error) {
    console.error(`âŒ loadDataFromFile: ÐžÑˆÐ¸Ð±ÐºÐ° Ð·Ð°Ð³Ñ€ÑƒÐ·ÐºÐ¸ ${filename}:`, error);

    // Fallback Ð½Ð° localStorage Ñ‚Ð¾Ð»ÑŒÐºÐ¾ Ð´Ð»Ñ ÐÐ• Ñ„Ð¾Ñ‚Ð¾Ð³Ñ€Ð°Ñ„Ð¸Ð¹
    if (filename !== 'photos.json') {
