// Ð¡Ð¾Ñ…Ñ€Ð°Ð½ÐµÐ½Ð¸Ðµ Ñ„Ð°Ð¹Ð»Ð° Ñ‡ÐµÑ€ÐµÐ· API (Ð½Ð°Ð¿Ñ€ÑÐ¼ÑƒÑŽ Ð² Ñ„Ð°Ð¹Ð» Ð¿Ñ€Ð¾ÐµÐºÑ‚Ð°)
const saveToProjectFile = async (data, filename) => {
  try {
    console.log(`ðŸ’¾ saveToProjectFile: Ð¡Ð¾Ñ…Ñ€Ð°Ð½ÐµÐ½Ð¸Ðµ ${filename}...`);
    console.log(`ðŸ’¾ saveToProjectFile: Ð”Ð°Ð½Ð½Ñ‹Ðµ (Ð¿ÐµÑ€Ð²Ñ‹Ðµ 100 ÑÐ¸Ð¼Ð²Ð¾Ð»Ð¾Ð²):`, JSON.stringify(data).substring(0, 100));

