    });
      console.log(`ðŸ’¾ saveToProjectFile: ÐžÑ‚Ð²ÐµÑ‚ Ð¿Ð¾Ð»ÑƒÑ‡ÐµÐ½:`, response.status, response.statusText);
    } catch (fetchError) {
