// Ð—Ð°Ð³Ñ€ÑƒÐ·ÐºÐ° Ð´Ð°Ð½Ð½Ñ‹Ñ… Ð¸Ð· Ñ„Ð°Ð¹Ð»Ð° Ñ‡ÐµÑ€ÐµÐ· fetch
export const loadDataFromFile = async (filename) => {
  try {
    // Ð˜ÑÐ¿Ð¾Ð»ÑŒÐ·ÑƒÐµÐ¼ timestamp Ð´Ð»Ñ Ð¿Ñ€ÐµÐ´Ð¾Ñ‚Ð²Ñ€Ð°Ñ‰ÐµÐ½Ð¸Ñ ÐºÐµÑˆÐ¸Ñ€Ð¾Ð²Ð°Ð½Ð¸Ñ
    const timestamp = Date.now();
    const url = `/${filename}?t=${timestamp}`;
    console.log(`ðŸ“¥ loadDataFromFile: Ð—Ð°Ð³Ñ€ÑƒÐ·ÐºÐ° ${filename} Ñ URL: ${url}`);

    // Ð˜ÑÐ¿Ð¾Ð»ÑŒÐ·ÑƒÐµÐ¼ cache: 'no-store' Ð´Ð»Ñ Ð¿Ñ€ÐµÐ´Ð¾Ñ‚Ð²Ñ€Ð°Ñ‰ÐµÐ½Ð¸Ñ ÐºÐµÑˆÐ¸Ñ€Ð¾Ð²Ð°Ð½Ð¸Ñ
    const response = await fetch(url, {
      cache: 'no-store',
      headers: {
        'Cache-Control': 'no-cache, no-store, must-revalidate',
        'Pragma': 'no-cache',
        'Expires': '0'
      }
    });

    if (!response.ok) {
      console.error(`âŒ loadDataFromFile: HTTP Ð¾ÑˆÐ¸Ð±ÐºÐ° ${response.status} Ð´Ð»Ñ ${filename}`);
      throw new Error(`HTTP error! status: ${response.status}`);
    }

    const data = await response.json();
    console.log(`âœ… loadDataFromFile: ${filename} Ð·Ð°Ð³Ñ€ÑƒÐ¶ÐµÐ½, ÑÐ»ÐµÐ¼ÐµÐ½Ñ‚Ð¾Ð²:`, Array.isArray(data) ? data.length : 'Ð½Ðµ Ð¼Ð°ÑÑÐ¸Ð²');

    // ÐÐ• ÑÐ¾Ñ…Ñ€Ð°Ð½ÑÐµÐ¼ Ñ„Ð¾Ñ‚Ð¾Ð³Ñ€Ð°Ñ„Ð¸Ð¸ Ð² localStorage (Ð¾Ð½Ð¸ ÑÐ»Ð¸ÑˆÐºÐ¾Ð¼ Ð±Ð¾Ð»ÑŒÑˆÐ¸Ðµ)
    // Ð”Ð»Ñ Ð´Ñ€ÑƒÐ³Ð¸Ñ… Ñ„Ð°Ð¹Ð»Ð¾Ð² ÑÐ¸Ð½Ñ…Ñ€Ð¾Ð½Ð¸Ð·Ð¸Ñ€ÑƒÐµÐ¼ Ñ localStorage
    if (filename !== 'theme.json' && filename !== 'photos.json') {
      const storageKey = `restaurant-${filename.replace('.json', '')}`;
      try {
      localStorage.setItem(storageKey, JSON.stringify(data));
      } catch (storageError) {
        console.warn(`âš ï¸ loadDataFromFile: ÐÐµ ÑƒÐ´Ð°Ð»Ð¾ÑÑŒ ÑÐ¾Ñ…Ñ€Ð°Ð½Ð¸Ñ‚ÑŒ ${filename} Ð² localStorage:`, storageError);
      }
    }

    return { success: true, data };
  } catch (error) {
    console.error(`âŒ loadDataFromFile: ÐžÑˆÐ¸Ð±ÐºÐ° Ð·Ð°Ð³Ñ€ÑƒÐ·ÐºÐ¸ ${filename}:`, error);

    // Fallback Ð½Ð° localStorage Ñ‚Ð¾Ð»ÑŒÐºÐ¾ Ð´Ð»Ñ ÐÐ• Ñ„Ð¾Ñ‚Ð¾Ð³Ñ€Ð°Ñ„Ð¸Ð¹
    if (filename !== 'photos.json') {
    const stored = localStorage.getItem(`restaurant-${filename.replace('.json', '')}`);
    if (stored) {
      try {
        const parsed = JSON.parse(stored);
          console.log(`ðŸ“¥ loadDataFromFile: Ð˜ÑÐ¿Ð¾Ð»ÑŒÐ·Ð¾Ð²Ð°Ð½ fallback Ð¸Ð· localStorage Ð´Ð»Ñ ${filename}`);
        return { success: true, data: parsed };
      } catch (e) {
          console.error(`âŒ loadDataFromFile: ÐžÑˆÐ¸Ð±ÐºÐ° Ð¿Ð°Ñ€ÑÐ¸Ð½Ð³Ð° Ð´Ð°Ð½Ð½Ñ‹Ñ… Ð¸Ð· localStorage Ð´Ð»Ñ ${filename}:`, e);
        return { success: false, message: 'ÐžÑˆÐ¸Ð±ÐºÐ° Ð¿Ð°Ñ€ÑÐ¸Ð½Ð³Ð° Ð´Ð°Ð½Ð½Ñ‹Ñ…' };
        }
      }
    }

    return { success: false, message: 'Ð¤Ð°Ð¹Ð» Ð½Ðµ Ð½Ð°Ð¹Ð´ÐµÐ½' };
  }
};

