      });

      updatedItems = menuItems.map(menuItem => 
        menuItem.id === editingItem.id ? { ...formattedItem, id: editingItem.id } : menuItem
      );
      showAlert('–ë–ª—é–¥–æ —É—Å–ø–µ—à–Ω–æ –æ–±–Ω–æ–≤–ª–µ–Ω–æ', 'success');
    } else {
      // –î–æ–±–∞–≤–ª–µ–Ω–∏–µ –Ω–æ–≤–æ–≥–æ –±–ª—é–¥–∞
      const newItem = {
