// Утилита для работы с данными через статические JSON файлы
// Данные загружаются через fetch и сохраняются через скачивание файлов

// Сохранение данных через скачивание файла (без API)
const saveToProjectFile = async (data, filename) => {
  try {
    // Форматируем JSON с отступами для читаемости
    const jsonString = JSON.stringify(data, null, 2);

    // Создаем Blob с JSON данными
    const blob = new Blob([jsonString], {
      type: 'application/json'
    });

    // Создаем URL для скачивания
    const url = URL.createObjectURL(blob);

    // Создаем временную ссылку для скачивания
    const link = document.createElement('a');
    link.href = url;
    link.download = filename;
    link.style.display = 'none';

    // Добавляем ссылку в DOM, кликаем и удаляем
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);

    // Освобождаем URL
    URL.revokeObjectURL(url);
    return {
      success: true,
      message: `Файл ${filename} скачан. Загрузите его на сервер в папку public/`
    };
  } catch (error) {
    console.error(`Ошибка скачивания ${filename}:`, error);
    return {
      success: false,
      message: `Не удалось скачать файл: ${error.message}`
    };
  }
};

// Загрузка данных из файла через fetch
export const loadDataFromFile = async filename => {
  try {
    // Используем cache: 'no-store' для предотвращения кеширования
    const response = await fetch(`/${filename}?t=${Date.now()}`, {
      cache: 'no-store',
      headers: {
        'Cache-Control': 'no-cache, no-store, must-revalidate',
        'Pragma': 'no-cache',
        'Expires': '0'
      }
    });
    if (!response.ok) {
      throw new Error(`HTTP error! status: ${response.status}`);
    }
    const data = await response.json();
    // Синхронизируем с localStorage после успешной загрузки из файла
    // Для theme.json используем специальную обработку в loadTheme
    if (filename !== 'theme.json') {
      const storageKey = `restaurant-${filename.replace('.json', '')}`;
      localStorage.setItem(storageKey, JSON.stringify(data));
    }
    return {
      success: true,
      data
    };
  } catch (error) {
    console.error(`Ошибка загрузки ${filename}:`, error);
    // Fallback на localStorage, если файл не найден
    const stored = localStorage.getItem(`restaurant-${filename.replace('.json', '')}`);
    if (stored) {
      try {
        const parsed = JSON.parse(stored);
        return {
          success: true,
          data: parsed
        };
      } catch (e) {
        console.error(`Ошибка парсинга данных из localStorage для ${filename}:`, e);
        return {
          success: false,
          message: 'Ошибка парсинга данных'
        };
      }
    }
    return {
      success: false,
      message: 'Файл не найден'
    };
  }
};

