// –ó–∞–≥—Ä—É–∑–∫–∞ –¥–∞–Ω–Ω—ã—Ö –∏–∑ —Ñ–∞–π–ª–∞ —á–µ—Ä–µ–∑ fetch
export const loadDataFromFile = async (filename) => {
  try {
    // –ò—Å–ø–æ–ª—å–∑—É–µ–º timestamp –¥–ª—è –ø—Ä–µ–¥–æ—Ç–≤—Ä–∞—â–µ–Ω–∏—è –∫–µ—à–∏—Ä–æ–≤–∞–Ω–∏—è
    const timestamp = Date.now();
    const url = `/${filename}?t=${timestamp}`;
    console.log(`üì• loadDataFromFile: –ó–∞–≥—Ä—É–∑–∫–∞ ${filename} —Å URL: ${url}`);

    // –ò—Å–ø–æ–ª—å–∑—É–µ–º cache: 'no-store' –¥–ª—è –ø—Ä–µ–¥–æ—Ç–≤—Ä–∞—â–µ–Ω–∏—è –∫–µ—à–∏—Ä–æ–≤–∞–Ω–∏—è
    const response = await fetch(url, {
      cache: 'no-store',
      headers: {
        'Cache-Control': 'no-cache, no-store, must-revalidate',
        'Pragma': 'no-cache',
        'Expires': '0'
      }
    });

    if (!response.ok) {
      console.error(`‚ùå loadDataFromFile: HTTP –æ—à–∏–±–∫–∞ ${response.status} –¥–ª—è ${filename}`);
      throw new Error(`HTTP error! status: ${response.status}`);
    }

    const data = await response.json();
    console.log(`‚úÖ loadDataFromFile: ${filename} –∑–∞–≥—Ä—É–∂–µ–Ω, —ç–ª–µ–º–µ–Ω—Ç–æ–≤:`, Array.isArray(data) ? data.length : '–Ω–µ –º–∞—Å—Å–∏–≤');

    // –ù–ï —Å–æ—Ö—Ä–∞–Ω—è–µ–º —Ñ–æ—Ç–æ–≥—Ä–∞—Ñ–∏–∏ –≤ localStorage (–æ–Ω–∏ —Å–ª–∏—à–∫–æ–º –±–æ–ª—å—à–∏–µ)
    // –î–ª—è –¥—Ä—É–≥–∏—Ö —Ñ–∞–π–ª–æ–≤ —Å–∏–Ω—Ö—Ä–æ–Ω–∏–∑–∏—Ä—É–µ–º —Å localStorage
    if (filename !== 'theme.json' && filename !== 'photos.json') {
      const storageKey = `restaurant-${filename.replace('.json', '')}`;
      try {
      localStorage.setItem(storageKey, JSON.stringify(data));
      } catch (storageError) {
        console.warn(`‚ö†Ô∏è loadDataFromFile: –ù–µ —É–¥–∞–ª–æ—Å—å —Å–æ—Ö—Ä–∞–Ω–∏—Ç—å ${filename} –≤ localStorage:`, storageError);
      }
    }

    return { success: true, data };
  } catch (error) {
    console.error(`‚ùå loadDataFromFile: –û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ ${filename}:`, error);

    // Fallback –Ω–∞ localStorage —Ç–æ–ª—å–∫–æ –¥–ª—è –ù–ï —Ñ–æ—Ç–æ–≥—Ä–∞—Ñ–∏–π
    if (filename !== 'photos.json') {
    const stored = localStorage.getItem(`restaurant-${filename.replace('.json', '')}`);
    if (stored) {
      try {
        const parsed = JSON.parse(stored);
          console.log(`üì• loadDataFromFile: –ò—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω fallback –∏–∑ localStorage –¥–ª—è ${filename}`);
        return { success: true, data: parsed };
      } catch (e) {
          console.error(`‚ùå loadDataFromFile: –û—à–∏–±–∫–∞ –ø–∞—Ä—Å–∏–Ω–≥–∞ –¥–∞–Ω–Ω—ã—Ö –∏–∑ localStorage –¥–ª—è ${filename}:`, e);
        return { success: false, message: '–û—à–∏–±–∫–∞ –ø–∞—Ä—Å–∏–Ω–≥–∞ –¥–∞–Ω–Ω—ã—Ö' };
        }
      }
    }

    return { success: false, message: '–§–∞–π–ª –Ω–µ –Ω–∞–π–¥–µ–Ω' };
  }
};

// –ó–∞–≥—Ä—É–∑–∫–∞ —Ç–µ–º—ã –∏–∑ —Ñ–∞–π–ª–∞
export const loadTheme = async () => {
  const result = await loadDataFromFile('theme.json');
  if (result.success) {
    const themeData = result.data;
    const currentThemeId = localStorage.getItem('restaurant-theme') || 'default';
    const newThemeId = themeData.currentTheme || 'default';

    // –°–∏–Ω—Ö—Ä–æ–Ω–∏–∑–∏—Ä—É–µ–º —Å localStorage –≤ –ø—Ä–∞–≤–∏–ª—å–Ω–æ–º —Ñ–æ—Ä–º–∞—Ç–µ
    if (themeData.currentTheme) {
      localStorage.setItem('restaurant-theme', themeData.currentTheme);
    }
    if (themeData.themes) {
      localStorage.setItem('restaurant-themes', JSON.stringify(themeData.themes));
    }

    // –ï—Å–ª–∏ —Ç–µ–º–∞ –∏–∑–º–µ–Ω–∏–ª–∞—Å—å, –ø—Ä–∏–º–µ–Ω—è–µ–º –µ—ë (–Ω–æ –Ω–µ –ø–µ—Ä–µ–∑–∞–ø–∏—Å—ã–≤–∞–µ–º, –µ—Å–ª–∏ —É–∂–µ –ø—Ä–∏–º–µ–Ω–µ–Ω–∞)
    if (newThemeId !== currentThemeId) {
      // –¢–µ–º–∞ –∏–∑–º–µ–Ω–∏–ª–∞—Å—å –≤ —Ñ–∞–π–ª–µ, –Ω–æ –Ω–µ –ø—Ä–∏–º–µ–Ω—è–µ–º –∑–¥–µ—Å—å - –ø—É—Å—Ç—å Header.js —ç—Ç–æ —Å–¥–µ–ª–∞–µ—Ç
      // —á—Ç–æ–±—ã –∏–∑–±–µ–∂–∞—Ç—å –∫–æ–Ω—Ñ–ª–∏–∫—Ç–æ–≤ —Å —É–∂–µ –ø—Ä–∏–º–µ–Ω–µ–Ω–Ω–æ–π —Ç–µ–º–æ–π
    }

    return themeData;
  }
  // –í–æ–∑–≤—Ä–∞—â–∞–µ–º –¥–µ—Ñ–æ–ª—Ç–Ω—É—é —Ç–µ–º—É
  const defaultTheme = {
    currentTheme: 'default',
    themes: {
      elegant: {
        id: 'elegant',
        name: '–≠–ª–µ–≥–∞–Ω—Ç–Ω–∞—è —Å–∏–Ω—è—è',
        primaryColor: '#2C3E50',
        primaryLight: '#34495E',
        primaryDark: '#1C2833',
        secondaryColor: '#3498DB',
        secondaryLight: '#5DADE2',
        secondaryDark: '#2980B9',
        accentColor: '#E74C3C',
        backgroundColor: '#ECF0F1',
        textColor: '#2C3E50',
        textLight: '#566573'
      }
    }
  };
  // –°–∏–Ω—Ö—Ä–æ–Ω–∏–∑–∏—Ä—É–µ–º –¥–µ—Ñ–æ–ª—Ç–Ω—É—é —Ç–µ–º—É —Å localStorage
  localStorage.setItem('restaurant-theme', defaultTheme.currentTheme);
  localStorage.setItem('restaurant-themes', JSON.stringify(defaultTheme.themes));
  return defaultTheme;
};

