// Ð—Ð°Ð³Ñ€ÑƒÐ·ÐºÐ° Ð´Ð°Ð½Ð½Ñ‹Ñ… Ð¸Ð· Ñ„Ð°Ð¹Ð»Ð° Ñ‡ÐµÑ€ÐµÐ· fetch
export const loadDataFromFile = async (filename) => {
  try {
    // Ð˜ÑÐ¿Ð¾Ð»ÑŒÐ·ÑƒÐµÐ¼ cache: 'no-store' Ð´Ð»Ñ Ð¿Ñ€ÐµÐ´Ð¾Ñ‚Ð²Ñ€Ð°Ñ‰ÐµÐ½Ð¸Ñ ÐºÐµÑˆÐ¸Ñ€Ð¾Ð²Ð°Ð½Ð¸Ñ
    const response = await fetch(`/${filename}?t=${Date.now()}`, {
      cache: 'no-store',
      headers: {
        'Cache-Control': 'no-cache, no-store, must-revalidate',
        'Pragma': 'no-cache',
        'Expires': '0'
      }
    });
    if (!response.ok) {
      throw new Error(`HTTP error! status: ${response.status}`);
    }
    const data = await response.json();
    // Ð¡Ð¸Ð½Ñ…Ñ€Ð¾Ð½Ð¸Ð·Ð¸Ñ€ÑƒÐµÐ¼ Ñ localStorage Ð¿Ð¾ÑÐ»Ðµ ÑƒÑÐ¿ÐµÑˆÐ½Ð¾Ð¹ Ð·Ð°Ð³Ñ€ÑƒÐ·ÐºÐ¸ Ð¸Ð· Ñ„Ð°Ð¹Ð»Ð°
    // Ð”Ð»Ñ theme.json Ð¸ÑÐ¿Ð¾Ð»ÑŒÐ·ÑƒÐµÐ¼ ÑÐ¿ÐµÑ†Ð¸Ð°Ð»ÑŒÐ½ÑƒÑŽ Ð¾Ð±Ñ€Ð°Ð±Ð¾Ñ‚ÐºÑƒ Ð² loadTheme
    if (filename !== 'theme.json') {
      const storageKey = `restaurant-${filename.replace('.json', '')}`;
      localStorage.setItem(storageKey, JSON.stringify(data));
    }
    return { success: true, data };
  } catch (error) {
    console.error(`ÐžÑˆÐ¸Ð±ÐºÐ° Ð·Ð°Ð³Ñ€ÑƒÐ·ÐºÐ¸ ${filename}:`, error);
    // Fallback Ð½Ð° localStorage, ÐµÑÐ»Ð¸ Ñ„Ð°Ð¹Ð» Ð½Ðµ Ð½Ð°Ð¹Ð´ÐµÐ½
    const stored = localStorage.getItem(`restaurant-${filename.replace('.json', '')}`);
    if (stored) {
      try {
        const parsed = JSON.parse(stored);
        return { success: true, data: parsed };
      } catch (e) {
        console.error(`ÐžÑˆÐ¸Ð±ÐºÐ° Ð¿Ð°Ñ€ÑÐ¸Ð½Ð³Ð° Ð´Ð°Ð½Ð½Ñ‹Ñ… Ð¸Ð· localStorage Ð´Ð»Ñ ${filename}:`, e);
        return { success: false, message: 'ÐžÑˆÐ¸Ð±ÐºÐ° Ð¿Ð°Ñ€ÑÐ¸Ð½Ð³Ð° Ð´Ð°Ð½Ð½Ñ‹Ñ…' };
      }
    }
    return { success: false, message: 'Ð¤Ð°Ð¹Ð» Ð½Ðµ Ð½Ð°Ð¹Ð´ÐµÐ½' };
  }
};

// Ð—Ð°Ð³Ñ€ÑƒÐ·ÐºÐ° Ñ‚ÐµÐ¼Ñ‹ Ð¸Ð· Ñ„Ð°Ð¹Ð»Ð°
export const loadTheme = async () => {
  const result = await loadDataFromFile('theme.json');
  if (result.success) {
    const themeData = result.data;
    const currentThemeId = localStorage.getItem('restaurant-theme') || 'default';
    const newThemeId = themeData.currentTheme || 'default';

    // Ð¡Ð¸Ð½Ñ…Ñ€Ð¾Ð½Ð¸Ð·Ð¸Ñ€ÑƒÐµÐ¼ Ñ localStorage Ð² Ð¿Ñ€Ð°Ð²Ð¸Ð»ÑŒÐ½Ð¾Ð¼ Ñ„Ð¾Ñ€Ð¼Ð°Ñ‚Ðµ
    if (themeData.currentTheme) {
      localStorage.setItem('restaurant-theme', themeData.currentTheme);
    }
    if (themeData.themes) {
      localStorage.setItem('restaurant-themes', JSON.stringify(themeData.themes));
    }

    // Ð•ÑÐ»Ð¸ Ñ‚ÐµÐ¼Ð° Ð¸Ð·Ð¼ÐµÐ½Ð¸Ð»Ð°ÑÑŒ, Ð¿Ñ€Ð¸Ð¼ÐµÐ½ÑÐµÐ¼ ÐµÑ‘ (Ð½Ð¾ Ð½Ðµ Ð¿ÐµÑ€ÐµÐ·Ð°Ð¿Ð¸ÑÑ‹Ð²Ð°ÐµÐ¼, ÐµÑÐ»Ð¸ ÑƒÐ¶Ðµ Ð¿Ñ€Ð¸Ð¼ÐµÐ½ÐµÐ½Ð°)
    if (newThemeId !== currentThemeId) {
      // Ð¢ÐµÐ¼Ð° Ð¸Ð·Ð¼ÐµÐ½Ð¸Ð»Ð°ÑÑŒ Ð² Ñ„Ð°Ð¹Ð»Ðµ, Ð½Ð¾ Ð½Ðµ Ð¿Ñ€Ð¸Ð¼ÐµÐ½ÑÐµÐ¼ Ð·Ð´ÐµÑÑŒ - Ð¿ÑƒÑÑ‚ÑŒ Header.js ÑÑ‚Ð¾ ÑÐ´ÐµÐ»Ð°ÐµÑ‚
      // Ñ‡Ñ‚Ð¾Ð±Ñ‹ Ð¸Ð·Ð±ÐµÐ¶Ð°Ñ‚ÑŒ ÐºÐ¾Ð½Ñ„Ð»Ð¸ÐºÑ‚Ð¾Ð² Ñ ÑƒÐ¶Ðµ Ð¿Ñ€Ð¸Ð¼ÐµÐ½ÐµÐ½Ð½Ð¾Ð¹ Ñ‚ÐµÐ¼Ð¾Ð¹
    }

    return themeData;
  }
  // Ð’Ð¾Ð·Ð²Ñ€Ð°Ñ‰Ð°ÐµÐ¼ Ð´ÐµÑ„Ð¾Ð»Ñ‚Ð½ÑƒÑŽ Ñ‚ÐµÐ¼Ñƒ
  const defaultTheme = {
    currentTheme: 'default',
    themes: {
      elegant: {
        id: 'elegant',
        name: 'Ð­Ð»ÐµÐ³Ð°Ð½Ñ‚Ð½Ð°Ñ ÑÐ¸Ð½ÑÑ',
        primaryColor: '#2C3E50',
        primaryLight: '#34495E',
        primaryDark: '#1C2833',
        secondaryColor: '#3498DB',
        secondaryLight: '#5DADE2',
        secondaryDark: '#2980B9',
        accentColor: '#E74C3C',
        backgroundColor: '#ECF0F1',
        textColor: '#2C3E50',
        textLight: '#566573'
      }
    }
  };
  // Ð¡Ð¸Ð½Ñ…Ñ€Ð¾Ð½Ð¸Ð·Ð¸Ñ€ÑƒÐµÐ¼ Ð´ÐµÑ„Ð¾Ð»Ñ‚Ð½ÑƒÑŽ Ñ‚ÐµÐ¼Ñƒ Ñ localStorage
  localStorage.setItem('restaurant-theme', defaultTheme.currentTheme);
  localStorage.setItem('restaurant-themes', JSON.stringify(defaultTheme.themes));
  return defaultTheme;
};

